import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper'
import Button from '@material-ui/core/Button';
import '../App.css';
import PlaylistRow from './PlaylistRow';

function ListItemLink(props) {
  return <ListItem button component = "a" {...props}/>;
}

class Scheduler extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            time: new Date().toLocaleString(),
            rows: [],
            playlists: props.data,
            count: 0
        };
    }

    componentDidMount() {
        // this.intervalID = setInterval(
        //     () => this.tick(),
        //     1000
        // );
    }
    componentWillUnmount() {
        clearInterval(this.intervalID);
    }

    tick() {
        // this.setState({
        //     time: new Date().toLocaleString()
        // });
    }

    handleAddRow = () => {
        this.setState((prevState, props) => {
            const row = {
                name: "",
                id: this.state.count + 1
            };
            return {
                rows: [...prevState.rows, row]
            };
        });
        const newCount = this.state.count + 1;
        this.setState({
            count: newCount
        })
    };

    handleDelete = (idx) => {
        const copyRows = Object.assign([], this.state.rows);
        copyRows.splice(idx, 1);
        this.setState({
            rows: copyRows
        });

        }

    handleChange = (idx) => evt => {
        // try {
        //      var item = {
        //          id: evt.target.id,
        //          name: evt.target.name,
        //          value: evt.target.value
        //      };
        //     console.log(item);
        //      var rowsArray = this.state.rows;
        //      var newRow = rowsArray.map((row, i) => {
        //              if (row.id === item.id) {
        //                  row[item.name] = item.value;
        //                  console.log(row[item.name]);
        //              }
        //          return row;
        //      });
        //      this.setState({
        //          rows: newRow
        //      });
        //      //   this.props.tableData(rowsArray);    
        //  } catch (error) {
        //      console.log("Error in React Table handle change : " + error);
        //  }
    }

    render() {
        console.log(this.state);

    return (
      <Card className="Card">
        <CardContent>
            <Button
                centered
                variant="contained"
                color="primary"
                className={''}
                onClick={this.handleAddRow}>
                    New Row
            </Button>
        </CardContent>
        <Paper className="ScrollList">
            <List component="nav" aria-label="main">
                    {this.state.rows && (
                        this.state.rows.map((d) => 
                            <PlaylistRow
                                id={d.id}
                                name={d.name}
                                playlists={this.state.playlists}
                                onChange={this.handleChange}
                                onDelete={this.handleDelete.bind(this, d.id)}/>
                        ))}
            </List>
        </Paper>

       </Card>
   )};
}

export default Scheduler;
