import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper'
import Button from '@material-ui/core/Button';
import '../App.css';
import PlaylistRow from './PlaylistRow';

function ListItemLink(props) {
  return <ListItem button component = "a" {...props}/>;
}

class Scheduler extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
            time: new Date().toLocaleString(),
            rows: [],
            playlists: props.data
        };
    }
    componentDidMount() {
        this.intervalID = setInterval(
            () => this.tick(),
            1000
        );
    }
    componentWillUnmount() {
        clearInterval(this.intervalID);
    }
    tick() {
        this.setState({
            time: new Date().toLocaleString()
        });
    }

    handleAddRow = () => {
        this.setState((prevState, props) => {
            const row = {
                name: "new row"
            };
            return {
                rows: [...prevState.rows, row]
            };
        });
    };

    handleDelete = (id) => {
        alert(IDBFactory);
         const items = this.state.rows.filter(item => item.id !== id);
         this.setState({
             rows: items
         });
     };


    render() {
        console.log(this.state);
    return (
      <Card className="Card">
        <CardContent>
            <Button
                centered
                variant="contained"
                color="primary"
                className={''}
                onClick={this.handleAddRow}>
                    New Row
            </Button>
        </CardContent>
        <Paper className="ScrollList">
            <List component="nav" aria-label="main">
                    {this.state.rows && (
                        this.state.rows.map((d) => 
                            <PlaylistRow id={d.uri} name={d.name} data={this.state.playlists} onDelete={this.handleDelete}/>
                        ))}
            </List>
        </Paper>

       </Card>
   )};
}

export default Scheduler;
