import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper'
import Button from '@material-ui/core/Button';
import '../App.css';
import PlaylistRow from './PlaylistRow';
import UniqueId from 'react-html-id';

function ListItemLink(props) {
  return <ListItem button component = "a" {...props}/>;
}

class Scheduler extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            time: new Date().toLocaleString(),
            rows: [],
            playlists: props.data,
            count: 0
        };
    }

    componentDidMount() {
        // this.intervalID = setInterval(
        //     () => this.tick(),
        //     1000
        // );
    }
    componentWillUnmount() {
        clearInterval(this.intervalID);
    }

    tick() {
        // this.setState({
        //     time: new Date().toLocaleString()
        // });
    }

    handleAddRow = () => {
        this.setState((prevState, props) => {
            const row = {
                id: this.state.count,
                name: "",
                start_time: "12:00",
                end_time: "12:30"
            };
            return {
                rows: [...prevState.rows, row]
            };
        });
        this.setState({count: (this.state.count + 1)})
    };

    handleDelete = (id, e) => {
        const copyRows = Object.assign([], this.state.rows);
        const index = this.state.rows.findIndex((row) => {
            return row.id === id
        });
        copyRows.splice(index, 1);
        this.setState({
            rows: copyRows
        });
    }

    handleChange = (id, e) => {
        const index = this.state.rows.findIndex((row) => {
            return row.id === id
        });

        const row = Object.assign({}, this.state.rows[index]);

        const key = e.target.name;
        row[key] = e.target.value;

        const rows = Object.assign([], this.state.rows);
        rows[index] = row;
        this.setState({
            rows: rows
        });
    }

    render() {
        console.log(this.state);

    return (
      <Card className="Card">
        <CardContent>
            <Button
                centered
                variant="contained"
                color="primary"
                className={''}
                onClick={this.handleAddRow}>
                    New Row
            </Button>
        </CardContent>
        <Paper className="ScrollList">
            <List component="nav" aria-label="main">
                    {this.state.rows && (
                        this.state.rows.map((d) => {
                            return (<PlaylistRow
                                id={d.id}
                                name={d.name}
                                start_time={d.start_time}
                                end_time={d.end_time}
                                playlists={this.state.playlists}
                                onChange={this.handleChange.bind(this, d.id)}
                                onDelete={this.handleDelete.bind(this, d.id)} />)
                        }
                        ))}
            </List>
        </Paper>

       </Card>
   )};
}

export default Scheduler;
